Register-ArgumentCompleter -Native -CommandName dumpbin -ScriptBlock {
  param ([string]$wordToComplete, [System.Management.Automation.Language.CommandAst]$commandAst, [int]$cursorPosition)
  @(if ($wordToComplete.StartsWith('/')) {
      '/ALL', '/ARCHIVEMEMBERS', '/CLRHEADER', '/DEPENDENTS', '/DIRECTIVES', '/DISASM', '/DISASM:BYTES', '/DISASM:NOBYTES', '/DISASM:NOWIDE', '/DISASM:WIDE', '/ERRORREPORT:NONE', '/ERRORREPORT:PROMPT', '/ERRORREPORT:QUEUE', '/ERRORREPORT:SEND', '/EXPORTS', '/FPO', '/HEADERS', '/IMPORTS', '/IMPORTS:', '/LINENUMBERS', '/LINKERMEMBER', '/LINKERMEMBER:1', '/LINKERMEMBER:2', '/LINKERMEMBER:4', '/LINKERMEMBER:8', '/LINKERMEMBER:16', '/LINKERMEMBER:32', '/LOADCONFIG', '/NOLOGO', '/NOPDB', '/NOSECTION:', '/OUT:', '/OTHERARCHEXPORTS', '/EXPORTS', '/OTHERARCHMETADATA:REDIRECTION', '/LOADCONFIG', '/OTHERARCHMETADATA:THUNKMAPPINGS', '/LOADCONFIG', '/OTHERARCHMETADATA:ALL', '/LOADCONFIG', '/OTHERLOADCONFIG', '/LOADCONFIG', '/PDATA', '/PDBPATH', '/PDBPATH:VERBOSE', '/RANGE:', '/RAWDATA', '/RAWDATA:NONE', '/RAWDATA:1', '/RAWDATA:2', '/RAWDATA:4', '/RAWDATA:8', '/RELOCATIONS', '/SECTION:', '/SUMMARY', '/SYMBOLS', '/TLS', '/UNWINDINFO'
    }).Where{ $_ -like "$wordToComplete*" }
}
