Register-ArgumentCompleter -Native -CommandName eslint -ScriptBlock {
  param([string]$wordToComplete)
  @(if ($wordToComplete.StartsWith('-')) {
      '--no-config-lookup', '-c', '--config=', '--inspect-config', '--ext=', '--ext=.jsx,.tsx', '--global=', '--parser=', '--parser-options=', '--plugin=', '--rule=', '--fix', '--fix-dry-run', '--fix-type=directive', '--fix-type=problem', '--fix-type=suggestion', '--fix-type=layout', '--no-ignore', '--ignore-pattern=', '--stdin', '--stdin-filename=', '--quiet', '--max-warnings=10', '--max-warnings=100', '-o', '--output-file=', '--format=', '-f', '--format=stylish', '--color', '--no-color', '--no-inline-config', '--report-unused-disable-directives', '--report-unused-disable-directives-severity=off', '--report-unused-disable-directives-severity=warn', '--report-unused-disable-directives-severity=error', '--report-unused-inline-configs=off', '--report-unused-inline-configs=warn', '--report-unused-inline-configs=error', '--cache', '--cache-file=', '--cache-file=.eslintcache', '--cache-location=', '--cache-strategy=content', '--cache-strategy=metadata', '--suppress-all', '--suppress-rule=', '--suppressions-location=', '--prune-suppressions', '--pass-on-unpruned-suppressions', '--init', '--env-info', '--no-error-on-unmatched-pattern', '--exit-on-fatal-error', '--no-warn-ignored', '--pass-on-no-patterns', '--debug', '-h', '--help', '-v', '--version', '--print-config=', '--stats', '--flag=', '--mcp'
    }).Where{ $_ -like "$wordToComplete*" }
}
